package com.samsung.android.app;

import android.content.Context;
import android.content.pm.PackageInfo;
import android.content.pm.PackageManager.NameNotFoundException;
import android.content.pm.Signature;
import android.os.Debug;
import android.util.SparseArray;
import com.samsung.android.util.SemLog;
import java.util.ArrayList;
import java.util.HashMap;

public class WhiteListForCategoryOrder {
    private static final boolean DEBUG = Debug.semIsProductDev();
    private static final int SIGNATURE_MAX = 3;
    private static final int SIGNATURE_SAMSUNG_APPLICATION = 2;
    private static final int SIGNATURE_SAMSUNG_PLATFORM = 1;
    private static final int SIGNATURE_SAMSUNG_SHARED = 4;
    private static final String TAG = "WhiteListForCategoryOrder";
    private static WhiteListForCategoryOrder sInstance = null;
    private SparseArray<Signature> mSignaturesMap = new SparseArray();
    private HashMap<String, Integer> mWhiteList = new HashMap();

    private void WhiteListForCategoryOrder() {
        setupSignaturesMap();
        setupWhiteList();
    }

    public static WhiteListForCategoryOrder getInstance() {
        if (sInstance == null) {
            sInstance = new WhiteListForCategoryOrder();
        }
        return sInstance;
    }

    private ArrayList<Signature> getSignatures(int i) {
        ArrayList<Signature> arrayList = new ArrayList();
        for (int i2 = 0; i2 < 3; i2++) {
            int i3 = 1 << i2;
            if ((i & i3) != 0) {
                arrayList.add((Signature) this.mSignaturesMap.get(i3));
            }
        }
        return arrayList;
    }

    private boolean hasSamsungKey(Signature[] signatureArr) {
        return isMatchedSignature(signatureArr, 7);
    }

    private boolean isMatchedSignature(Signature[] signatureArr, int i) {
        Iterable<Signature> signatures = getSignatures(i);
        if (!(signatureArr == null || signatures == null)) {
            for (Object obj : signatureArr) {
                for (Signature signature : signatures) {
                    if (signature != null && signature.equals(obj)) {
                        return true;
                    }
                }
            }
        }
        return false;
    }

    private void setupSignaturesMap() {
        this.mSignaturesMap.put(1, new Signature(""));
        this.mSignaturesMap.put(2, new Signature(""));
        this.mSignaturesMap.put(4, new Signature(""));
    }

    private void setupWhiteList() {
    }

    public boolean isAllowedToUseOrder(Context context, String str) {
        boolean isMatchedSignature;
        try {
            PackageInfo packageInfo = context.getPackageManager().getPackageInfo(str, 64);
            if (packageInfo == null) {
                if (DEBUG) {
                    SemLog.d(TAG, "isAllowedToUse : pkgInfo is null");
                }
                return false;
            } else if ((packageInfo.applicationInfo.flags & 129) != 0) {
                return true;
            } else {
                Signature[] signatureArr = packageInfo.signatures;
                if (hasSamsungKey(signatureArr)) {
                    return true;
                }
                Integer num = (Integer) this.mWhiteList.get(str);
                if (num == null) {
                    if (DEBUG) {
                        SemLog.d(TAG, "isAllowedToUse : executable manager white lists does not include this App");
                    }
                    return false;
                }
                isMatchedSignature = isMatchedSignature(signatureArr, num.intValue());
                if (!isMatchedSignature && DEBUG) {
                    SemLog.d(TAG, "isAllowedToUse : executable manager white lists don't match this App");
                }
                return isMatchedSignature;
            }
        } catch (NameNotFoundException e) {
            if (DEBUG) {
                SemLog.d(TAG, "isNotAllowedToUse : " + e);
            }
            isMatchedSignature = false;
        }
    }
}
